import React, { useEffect, useMemo, useState } from "react";
import { useAuth } from "../AuthContext";
import { api } from "../api";
import Timer from "../components/Timer";
import SessionTable from "../components/SessionTable";

export default function Dashboard() {
  const { token } = useAuth();
  const [sessions, setSessions] = useState([]);
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [summary, setSummary] = useState({ days: [], totalMinutes: 0 });

  async function load() {
    const params = new URLSearchParams();
    if (from) params.set("from", from);
    if (to) params.set("to", to);
    const list = await api(`/api/sessions?${params.toString()}`, { token });
    setSessions(list);
    const sum = await api(`/api/stats/summary?${params.toString()}`, { token });
    setSummary(sum);
  }

  useEffect(() => { load(); /* eslint-disable-next-line */ }, [from, to]);

  async function handleDelete(id) {
    await api(`/api/sessions/${id}`, { method: "DELETE", token });
    await load();
  }

  const totalHrs = useMemo(() => (summary.totalMinutes / 60).toFixed(2), [summary]);

  return (
    <div className="row">
      <div style={{ flex: 1, minWidth: 280 }}>
        <Timer onSaved={load} />
        <div className="spacer"></div>
        <div className="card">
          <h3 className="title">Filter</h3>
          <div className="row">
            <div style={{ flex: 1 }}>
              <label className="muted">From</label>
              <input className="input" type="date" value={from} onChange={e => setFrom(e.target.value)} />
            </div>
            <div style={{ flex: 1 }}>
              <label className="muted">To</label>
              <input className="input" type="date" value={to} onChange={e => setTo(e.target.value)} />
            </div>
            <div style={{ alignSelf: "end" }}>
              <button className="btn secondary" onClick={() => { setFrom(""); setTo(""); }}>Clear</button>
            </div>
          </div>
        </div>
        <div className="spacer"></div>
        <div className="card">
          <h3 className="title">Summary</h3>
          <p>Total time: <strong>{totalHrs} hrs</strong></p>
          <ul className="muted" style={{ marginTop: 8 }}>
            {summary.days.map(d => (
              <li key={d.date}>{d.date}: {(d.minutes/60).toFixed(2)} hrs</li>
            ))}
          </ul>
        </div>
      </div>
      <div style={{ flex: 2, minWidth: 360 }}>
        <SessionTable sessions={sessions} onDelete={handleDelete} />
      </div>
    </div>
  );
}
