import React from "react";

export default function SessionTable({ sessions, onDelete }) {
  function minsToHrs(mins) {
    const h = Math.floor(mins / 60);
    const m = mins % 60;
    return `${h}h ${m}m`;
  }
  return (
    <div className="card">
      <h3 className="title">Sessions</h3>
      <table>
        <thead>
          <tr>
            <th>Date</th>
            <th>Title</th>
            <th>Duration</th>
            <th>Notes</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {sessions.map(s => (
            <tr key={s.id}>
              <td>{s.date}</td>
              <td>{s.title}</td>
              <td>{minsToHrs(Number(s.durationMinutes))}</td>
              <td className="muted">{s.notes}</td>
              <td><button className="btn danger" onClick={() => onDelete(s.id)}>Delete</button></td>
            </tr>
          ))}
          {sessions.length === 0 && (
            <tr><td colSpan={5} className="muted">No sessions yet</td></tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
