import React from "react";
import { Routes, Route, Navigate, Link, useNavigate } from "react-router-dom";
import AuthProvider, { useAuth } from "./AuthContext";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";

function Private({ children }) {
  const { token } = useAuth();
  return token ? children : <Navigate to="/login" replace />;
}

function NavBar() {
  const { user, logout } = useAuth();
  const nav = useNavigate();
  return (
    <div className="nav">
      <Link to="/" className="title">ðŸ“š Study Time Tracker</Link>
      <div>
        {user ? (
          <>
            <span className="muted" style={{ marginRight: 12 }}>Hi, {user.name || user.email}</span>
            <button className="btn secondary" onClick={() => { logout(); nav("/login"); }}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login" className="btn secondary" style={{ marginRight: 8 }}>Login</Link>
            <Link to="/register" className="btn">Register</Link>
          </>
        )}
      </div>
    </div>
  );
}

export default function App() {
  return (
    <AuthProvider>
      <div className="container">
        <NavBar />
        <Routes>
          <Route path="/" element={<Private><Dashboard /></Private>} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
        </Routes>
      </div>
    </AuthProvider>
  );
}
